#include "Login.hpp"
#include "Primary.hpp"
#include "People.hpp"

Login::Login(vector<string> input_line, People* user) : user(user), input_line(input_line) {}

void Login::checkNotInLoginMode() {
    try {
        if(user == nullptr) {
            loginModeInput();
        }
        else {
            throw runtime_error(PERMISSION_DENIED);
        }
    } catch(runtime_error& ex) {
        catchError(ex);
    }
}

void Login::checkQuestionMarkLogin() {
    try {
        if(input_line[2] == QUESTION_MARK) {
            checkNotInLoginMode();
        }
        else {
            throw runtime_error(BAD_REQUEST);
        }
    } catch(runtime_error& ex) {
        catchError(ex);
    }
}

void Login::checkInputSizeLogin() {
    try {
        if(input_line.size() == LOGIN_MODE_SIZE) {
            checkQuestionMarkLogin();
        }
        else {
            throw runtime_error(BAD_REQUEST);
        }
    } catch(runtime_error& ex) {
        catchError(ex);
    }
}
