#include "Login.hpp"
#include "Primary.hpp"

Login::Login(vector<string> input_line, People* user, vector<People*> people) : people(people), user(user), input_line(input_line) {}

bool Login::checkValidPassword(string id, string password) {
    for(int i = 0; i < people.size(); i++) {
        if(people[i]->getId() == id and people[i]->getPassword() == password) {
            return true;
        }
    }
    return false;
}

bool Login::checkValidId(string id) {
    for(int i = 0; i < people.size(); i++) {
        if(people[i]->getId() == id) {
            return true;
        }
    }
    return false;
}

void Login::findPeopleById(string id) {
    for(int i = 0; i < people.size(); i++) {
        if(people[i]->getId() == id) {
            user = people[i];
        }
    }
}

void Login::loginModeInput() {
    string id = NULL_STRING, password = NULL_STRING;
    for(int i = 3; i < 7; i += 2) {
        string input_type = input_line[i], value = input_line[i + 1];
        if(input_type == ID) {
            id = value;
        }
        else if(input_type == PASSWORD) {
            password = value;
        }
    }
    if(!checkValidId(id)) {
        throw runtime_error(NOT_FOUND);
    }
    else if(!checkValidPassword(id, password)) {
        throw runtime_error(PERMISSION_DENIED);
    }
    else {
        findPeopleById(id);
        throw runtime_error(OK);
    }
}

void Login::checkNotInLoginMode() {

    cout << "ehurfhr" << endl;
    if(user == nullptr) {
        loginModeInput();
    }
    else {
        throw runtime_error(PERMISSION_DENIED);
    }
}

void Login::checkQuestionMark() {
    if(input_line[2] == QUESTION_MARK) {
        checkNotInLoginMode();
    }
    else {
        throw runtime_error(BAD_REQUEST);
    }
}

void Login::checkInputSize() {
    if(input_line.size() == LOGIN_MODE_SIZE) {
        checkQuestionMark();
    }
    else {
        throw runtime_error(BAD_REQUEST);
    }
}
