#include "University.hpp"
#include "Primary.hpp"


void University::checkValidInputPersonalPage() {
    try {
        if(input_line[3] != ID) {
            throw runtime_error(BAD_REQUEST);
        }
        else if(!isNumber(input_line[4])) {
            throw runtime_error(BAD_REQUEST);
        }
        else if(!checkValidId(input_line[4])) {
            throw runtime_error(NOT_FOUND);
        }
        else {
            user->personalPage();
        }
    } catch(runtime_error& ex) {
        catchError(ex);
    }
}

void University::checkQuestionMarkPersonalPage() {
    try {
        if(input_line[2] == QUESTION_MARK) {
            checkValidInputPersonalPage();
        }
        else {
            throw runtime_error(BAD_REQUEST);
        }
    } catch(runtime_error& ex) {
        catchError(ex);
    }
}

void University::checkLoginPersonalPage() {
    try {
        if(checkLogin()) {
            checkQuestionMarkPersonalPage();
        }
        else {
            throw runtime_error(PERMISSION_DENIED);
        }
    } catch(runtime_error& ex) {
        catchError(ex);
    }
}

void University::runPersonalPage() {
    try {
        if(input_line.size() == REMOVE_POST_MODE_SIZE) {
            checkLoginPersonalPage();
        }
        else {
            throw runtime_error(BAD_REQUEST);
        }
    } catch(runtime_error& ex) {
        catchError(ex);
    }
}
