#include "University.hpp"
#include "Primary.hpp"

void shareCourseModeInput() {
    string id = NULL_STRING, password = NULL_STRING;
    for(int i = 3; i < 7; i += 2) {
        string input_type = input_line[i], value = input_line[i + 1];
        if(input_type == ID) {
            id = value;
        }
        else if(input_type == PASSWORD) {
            password = value;
        }
    }
}

void University::runShareCourse() {
    course_offer_id++;
    if(input_line.size() != SHARE_COURSE_MODE_SIZE) {
        throw runtime_error(BAD_REQUEST);
    }
    else if(!checkLogin()) {
        throw runtime_error(PERMISSION_DENIED);
    }
    else if(!isQuestionMark(input_line[2])) {
        throw runtime_error(BAD_REQUEST);
    }
    else {
        shareCourseModeInput();
    }
}
