#ifndef TIME_INCLUDE
#define TIME_INCLUDE

#include "Global.hpp"

enum WeekDay{
    SUNDAY,
    MONDAY,
    TUESDAY,
    WEDNESDAY,
    THURSDAY,
    FRIDAY,
    SATURDAY
};

class Time {
public:
    Time(string complete_time);
    ~Time() {};
    string getCompleteTime() {return complete_time;}
    WeekDay getWeekDay() {return weekday;}
    int getStart() {return start;}
    int getEnd() {return end;}
protected:
private:
    string complete_time;
    WeekDay weekday;
    int start, end;
};

#endif
