#include "Time.hpp"
#include "Primary.hpp"

Time::Time(string complete_time) : complete_time(complete_time) {
    weekday = NULL_STRING;
    string resultStart = NULL_STRING, resultEnd = NULL_STRING;
    bool flagColon = false, flagDash = false;
    for(char c : complete_time) {
        if(c == COLON) {
            flagColon = true;
        }
        else if(c == DASH) {
            flagDash = true;
        }
        else if(!flagColon) {
            weekday += c;
        }
        else if(!flagDash) {
            resultStart += c;
        }
        else {
            resultEnd += c;
        }
    }
    start = stoi(resultStart), end = stoi(resultEnd);
}
