#ifndef COURSE_OFFER_INCLUDE
#define COURSE_OFFER_INCLUDE

#include "Global.hpp"
#include "Time.hpp"

class CourseOffer {
public:
    CourseOffer(int id, string course_id, string professor_id, int capacity, string time, string exam_time, int class_number);
    ~CourseOffer() {};
    int getId() {return id;}
    int getClassNumber() {return class_number;}
    int getCapacity() {return capacity;}
    string getCourseId() {return course_id;}
    string getProfessorId() {return professor_id;}
    string getTime() {return time.getCompleteTime();}
    string getExamTime() {return exam_time;}
    int getClassNumber() {return class_number;}
protected:
    string course_id, professor_id;
    Time time;
    exam_time;
    int id, class_number, capacity;
private:
};

#endif
