    cout << name << " " << major << " " << semester << " ";
    for(int i = 0; i + 1 < course_offers.size(); i++) {
        cout << course_offers[i]->getName() << COMMA;
    }
    if(!course_offers.empty()) {
        cout << course_offers.back()->getName() << endl;
    }

}
