#include "University.hpp"
#include "Primary.hpp"

void University::runGetMyCourse() {
    if(input_line.size() != GET_MY_COURSE_MODE_SIZE) {
        throw runtime_error(BAD_REQUEST);
    }
    else if(!checkLogin()) {
        throw runtime_error(PERMISSION_DENIED);
    }
    else if(!isQuestionMark(input_line[2])) {
        throw runtime_error(BAD_REQUEST);
    }
    else if(user->numberOfCourses() == 0) {
        throw runtime_error(EMPTY);
    }
    else {
        vector<CourseOffer*> course_off = user->getCourseOffers();
        for(int i = 0; i < course_off.size(); i++) {
            cout << course_off[i]->getCourseOfferId() << " ";
            cout << course_off[i]->getName() << " ";
            cout << course_off[i]->getCapacity() << " ";
            cout << people[findPeopleIndexById(course_off[i]->getProfessorId())]->getName()  << " ";
            cout << course_off[i]->getTime() << " ";
            cout << course_off[i]->getExamTime() << " ";
            cout << course_off[i]->getClassNumber() << endl;
        }
    }
}
