    void showPosts(vector<string>& output);
    void addNotification(string notification_line);
    void printNotification(vector<string>& output);
    bool emptyNotification();
protected:
    int post_number;
    string profile_image;
    vector<Post*> posts;
    vector<string> notifications;
private:
};

#endif
