#include "University.hpp"
#include "Primary.hpp"

void University::validGetPostInput() {
    string id = NULL_STRING, post_id = NULL_STRING;
    for(int i = 3; i < 7; i += 2) {
        string input_type = input_line[i], value = input_line[i + 1];
        if(input_type == ID) {
            id = value;
        }
        else if(input_type == POST_ID) {
            post_id = value;
        }
    }
    if(!isNumber(id) || !isNumber(post_id)) {
        throw runtime_error(BAD_REQUEST);
    }
    else if(stoi(post_id) == ZERO) {
        throw runtime_error(BAD_REQUEST);
    }
    else if(!checkValidId(id) || !people[findPeopleIndexById(id)]->isInPost(stoi(post_id))) {
        throw runtime_error(NOT_FOUND);
    }
    else {
        people[findPeopleIndexById(id)]->getPost(output, stoi(post_id));
    }
}

void University::runGetPost(string id, string post_id) {
    if(!checkLogin()) {
        throw runtime_error(PERMISSION_DENIED);
    }
    else if(user->getId() == ZERO_STRING) {
        throw runtime_error(PERMISSION_DENIED);
    }
    else {
        validGetPostInput();
    }
}
