#include "handlers.hpp"

LoginHandler::LoginHandler(University *university): university(university) {}

Response* LoginHandler::callback(Request* req) {
    try {
        string username = req->getBodyParam("username");
        string password = req->getBodyParam("password");
        university->runLogin(username, password);

        Response* res = Response::redirect("/rand");
        res->setSessionId("SID");
        return res;
    }
    catch (runtime_error& e) {
         std::string body;

        body += "<!DOCTYPE html>";
        body += "<html lang=\"en\">";

        body += "<head>";
        body += "  <title>Random Number Page</title>";
        body += "</head>";

        body += "<body style=\"text-align: center;\">";
        body += "  <h1>AP HTTP</h1>";
        body += "  <p>A random number in [1, 10] is: " + randomNumber + "</p>";
        body += "  <p>SessionId: " + req->getSessionId() + "</p>";
        body += "</body>";

        body += "</html>";
        res->setBody(body);
        return res;
        if(e.what() == PERMISSION_DENIED) {

            Response* res = Response::redirect("/permissionDenied");
            return res;
        }
        else if(e.what() == BAD_REQUEST) {
            Response* res = Response::redirect("/badRequest");
            return res;
        }
        else if(e.what() == NOT_FOUND) {
            Response* res = Response::redirect("/notFound");
            return res;
        }
        else if(e.what() == EMPTY) {
            Response* res = Response::redirect("/empty");
            return res;
        }
    }
}
