    body += ".image_part{width: 40%;}";
    body += ".info_part {width: 70%;font-size: 28px;height: 60px;}";
    body += ".info_part_object {width: 80%;margin-left: 10%;background-color: rgba(241, 204, 149, 0.804);margin-bottom: 25px;text-align: left;padding-left: 15px;border-style:solid;border-width: 1px;border-color: rgb(97, 82, 58);}";
    body += "#title_info_part{font-style:italic;font-weight: bold;}";
    body += "hr.new4 {margin-top: 30px;border: 2px solid rgb(97, 82, 58);}";
    body += "hr.new5 {margin-top: 6px;border: 2px solid rgb(97, 82, 58);}";
    body += ".options_in_student{display: flex;}";
    body += ".object_in_options_student{width: 41%;margin-top: 30px;height: 60px;font-size: 25px;padding-top: 20px;margin-left: 6%;background-color: rgb(97, 82, 58);color: aliceblue;}";
    body += ".submit_class{width: 100%;height: 100px;}";
    body += ".logout_key{height: 40px;border: none;font-family: fantasy;font-size: 25px;color: aliceblue;background-color: rgb(97, 82, 58);}";
    body += "#myDIV {width: 100%;padding: 50px 0;text-align: center;background-color: lightblue;margin-top: 20px;display: none;}";
	body += "</style>";
    body += "</head>";
    body += "<body>";
    body += "<div class='info'><div class='image_part'><img src='" + university->getUserProfile() + "' class='Uni_pic' alt='Home Logo'></div><div class='info_part'><div id='title_info_part'>information</div><div class='info_part_object'>Name : " + university->getUserName() + "</div><div class='info_part_object'>Id : " + university->getUserId() + "</div><div class='info_part_object'>Major : " +  university->getUserMajor() + "</div></div></div>";
    body += "<hr class='new4'><hr class='new5'><hr class='new5'>";
    body += "<div class='options_in_student'><div class='object_in_options_student'><button class='logout_key' onclick='myFunction()'>Show course by id</button></div><div class='object_in_options_student'>Show courses</div></div>";
    body += "<div id='myDIV'><section class='login_form'><form action='/GetCourseByIdHandler' method='post' ><input name='id' type='text' placeholder='Id' class='options_class' id='username_id'><input type='submit' class='submit_class' value='GetCourseByIdHandler'></form></section></div>";
    body += "<div class='options_in_student'><div class='object_in_options_student'>Put course</div><div class='object_in_options_student'>Delete course</div></div>";
    body += "<div class='options_in_student'><div class='object_in_options_student'>Change profile</div><div class='object_in_options_student'>Show personal page by id</div></div>";
    body += "<div class='options_in_student'>";
    body += "<div class='object_in_options_student'>Share post</div>";
    body += "<form class='object_in_options_student' action='/logout' method='post' >";
    body += "<input type='submit' class='logout_key' value='Logout'>";
    body += "</form>";
    body += "</div>";
    body += "<script>";
    body += "function myFunction() {";
    body += "var x = document.getElementById('myDIV');";
    body += "if (x.style.display === 'none') {";
    body += "x.style.display = 'block';";
    body += "} else {";
    body += "x.style.display = 'none';";
    body += "}";
    body += "}";
    body += "</script>";
    body += "</body>";
    body += "</html>";

    return body;
}


