#include "Global.hpp"
#include "University.hpp"
#include "server.hpp"
#include "handlers.hpp"

const string SHOW_SERVER_PORT = "Server running on port: ";


void mapServerPaths(Server* server, University* university) {
    server->setNotFoundErrPage("static/404.html");
    server->get("/", new ShowPage("static/home.html"));
    server->get("/home.png", new ShowImage("static/home.png"));
    server->get("/1.png", new ShowImage("static/1.png"));
    server->get("/login", new ShowPage("static/logincss.html"));
    server->post("/login", new LoginHandler(university));
    server->get("/logout", new ShowPage("static/logout.html"));
    server->post("/logout", new LogoutHandler(university));
    server->get("/getCourseById", new ShowPage("static/GetCourseByIdHandler.html"));
    server->post("/getCourseById", new GetCourseByIdHandler(university));
    server->get("/putCourse", new ShowPage("static/GetCourseByIdHandler.html"));
    server->post("/putCourse", new PutCourseHandler(university));
    server->get("/deleteCourse", new ShowPage("static/GetCourseByIdHandler.html"));
    server->post("/deleteCourse", new DeleteCourseHandler(university));
    server->get("/allCourses", new ShowPage("static/GetCourseByIdHandler.html"));
    server->post("/allCourses", new GetAllCoursesHandler(university));
    server->get("/personalPage", new ShowPage("static/PersonalPage.html"));
    server->post("/personalPage", new PersonalPageHandler(university));
    server->get("/notFound", new ShowPage("static/notFound.html"));
    server->get("/badRequest", new ShowPage("static/badRequest.html"));
    server->get("/permissionDenied", new ShowPage("static/permissionDenied.html"));
    server->get("/empty", new ShowPage("static/empty.html"));
    server->get("/student", new ShowPage("static/student.html"));
    server->get("/professor", new ShowPage("static/professor.html"));
    server->get("/ut_account", new ShowPage("static/ut_account.html"));
    server->get("/sharePost", new ShowPage("static/sharePost.html"));
    server->post("/sharePost", new SharePostHandler(university));
    server->get("/changeProfile", new ShowPage("static/changeProfile.html"));
    server->post("/changeProfile", new ChangeProfileHandler(university));
    server->get("/myCourses", new ShowPage("static/changeProfile.html"));
    server->post("/myCourses", new MyCoursesHandler(university));

    server->get("/courseOffer", new ShowPage("static/changeProfile.html"));
    server->post("/courseOffer", new CourseHandler(university));



}

int main(int argc, char *argv[]){
    int port = 8080;
    Server* server = new Server(port);
    University* university = new University(argv);
    mapServerPaths(server, university);
    cout << SHOW_SERVER_PORT << port << endl;
    server->run();
}
