#include "Global.hpp"
#include "University.hpp"
#include "server.hpp"
#include "handlers.hpp"

const string STATIC_HOME = "static/home.html";
const string STATIC_404 = "static/404.html";
const string STATIC_1 = "static/1.png";
const string STATIC_LOGIN = "static/logincss.html";
const string STATIC_NOT_FOUND = "static/notFound.html";
const string STATIC_BAD_REQUEST = "static/badRequest.html";
const string STATIC_PERMISSION_DENIED = "static/permissionDenied.html";
const string STATIC_EMPTY = "static/Empty.html";
const string STATIC_HOME = "static/home.html";
const string STATIC_404 = "static/404.html";

void mapServerPaths(Server* server, University* university) {
    server->setNotFoundErrPage(STATIC_404);
    server->get(HOME_PAGE, new ShowPage(STATIC_HOME));
    server->get(HOME_PNG, new ShowImage(STATIC_HOME));
    server->get(HOME_1, new ShowImage(STATIC_1));
    server->get(LOGIN_PAGE, new ShowPage(STATIC_LOGIN));
    server->post(LOGIN_PAGE, new LoginHandler(university));
    server->post(LOGOUT_PAGE, new LogoutHandler(university));
    server->post("/getCourseById", new GetCourseByIdHandler(university));
    server->post("/putCourse", new PutCourseHandler(university));
    server->post("/deleteCourse", new DeleteCourseHandler(university));
    server->post("/allCourses", new GetAllCoursesHandler(university));
    server->post("/personalPage", new PersonalPageHandler(university));
    server->get(NOT_FOUND_PAGE, new ShowPage(STATIC_NOT_FOUND));
    server->get(BAD_REQUEST_PAGE, new ShowPage(STATIC_BAD_REQUEST));
    server->get(PERMISSION_DENIED_PAGE, new ShowPage(STATIC_PERMISSION_DENIED));
    server->get(EMPTY_PAGE, new ShowPage(STATIC_EMPTY));
    server->get("/sharePost", new ShowPage("static/sharePost.html"));
    server->post("/sharePost", new SharePostHandler(university));
    server->get("/changeProfile", new ShowPage("static/changeProfile.html"));
    server->post("/changeProfile", new ChangeProfileHandler(university));
    server->post("/myCourses", new MyCoursesHandler(university));
    server->post("/courseOffer", new CourseOfferHandler(university));
    server->get(OK_PAGE, new ShowPage("static/ok.html"));
    server->post("/backHome", new BackHomeHandler(university));
}

int main(int argc, char *argv[]){
    int port = 8080;
    Server* server = new Server(port);
    University* university = new University(argv);
    mapServerPaths(server, university);
    cout << SHOW_SERVER_PORT << port << endl;
    server->run();
}
